<?php











namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => '0.1',
    'version' => '0.1.0.0',
    'aliases' => 
    array (
    ),
    'reference' => NULL,
    'name' => 'ramsterhad/deepdanboorutagassist',
  ),
  'versions' => 
  array (
    'ramsterhad/deepdanboorutagassist' => 
    array (
      'pretty_version' => '0.1',
      'version' => '0.1.0.0',
      'aliases' => 
      array (
      ),
      'reference' => NULL,
    ),
    'symfony/deprecation-contracts' => 
    array (
      'pretty_version' => 'v2.2.0',
      'version' => '2.2.0.0',
      'aliases' => 
      array (
      ),
      'reference' => '5fa56b4074d1ae755beb55617ddafe6f5d78f665',
